import React, { useState, useEffect } from 'react'
import { TouchableOpacity, Text, SafeAreaView, View, FlatList, StyleSheet } from 'react-native'
import PropTypes from 'prop-types'

import api from '../../services/api'
import { deleteUser } from '../../utils'
import ProductItem from '../../components/ProductItem'

import { Container, Title, Button, ButtonText, ProductList } from './styles'


const g1 = [
  {
    id: 'bd7acbea-c1b1-46c2-aed5-3ad53abb28ba',
    img: '../../images/politica1.jpg',
    title: 'First Item',
  },
  {
    id: '3ac68afc-c605-48d3-a4f8-fbd91aa97f63',
    img: '../../images/politica1.jpg',
    title: 'Second Item',
  },
  {
    id: '58694a0f-3da1-471f-bd96-145571e29d72',
    img: '../../images/politica1.jpg',
    title: 'Third Item',
  },
];

const ge = [
  {
    id: 'bd7acbea-c1b1-46c2-aed5-3ad53abb28ba',
    img: '../../images/esporte1.jpg',
    title: 'First Item',
  },
  {
    id: '3ac68afc-c605-48d3-a4f8-fbd91aa97f63',
    img: '../../images/esporte2.jpg',
    title: 'Second Item',
  },
  {
    id: '58694a0f-3da1-471f-bd96-145571e29d72',
    img: '../../images/esporte3.jpg',
    title: 'Third Item',
  },
];

const gshow = [
  {
    id: 'bd7acbea-c1b1-46c2-aed5-3ad53abb28ba',
    img: '../../images/famosos1.jpg',
    title: 'First Item',
  },
  {
    id: '3ac68afc-c605-48d3-a4f8-fbd91aa97f63',
    img: '../../images/famosos2.jpeg',
    title: 'Second Item',
  },
  {
    id: '58694a0f-3da1-471f-bd96-145571e29d72',
    img: '../../images/famosos3.jpg',
    title: 'Third Item',
  },
];

function Item({ title }) {
  return (
    <View style={styles.item}>
      <Text style={styles.title}>{title}</Text>
    </View>
  );
}


export default function Home() {
  // const [data, setData] = useState([]);
  // const [refreshing, setRefreshing] = useState(false);

  // useEffect(() => {
  //   async function loadProducts() {

  //     const response = await api.get('/products')

  //     console.log(response.data)

  //     setData(response.data.products);
  //   }

  //   loadProducts();
  // }, []);

  // renderListItem = ({ item }) => <ProductItem product={item} />

  return (
    <Container>
      {/* <ProductList
        // data={data}
        // keyExtractor={item => String(item.id)}
        // renderItem={renderListItem}
        // onRefresh={loadProducts}
        // refreshing={refreshing}
      /> */}

      <Title>G1</Title>
      <SafeAreaView style={styles.container}>
        <FlatList
          data={g1}
          renderItem={({ item }) => <img src={item.img} width="80" height="80" />}
          keyExtractor={item => item.id}
        />
      </SafeAreaView>

      <Title>GloboEsporte</Title>

      <Title>GShow</Title>
      <img src="../../images/famosos3.jpg" width="80" height="80" />
    

    </Container>
  );
}

Home.navigationOptions = ({ navigation }) => {

  return {
    title: 'Home',
    headerBackTitleVisible: true,
    headerRight: () => (
      <TouchableOpacity
        onPress={() => (
          deleteUser().then(() => {
            navigation.navigate('AuthLoading')
          })
        )}
        style={{ marginRight: 10 }}
      >
        <Text>Sair</Text>
      </TouchableOpacity>
    ),
  };
  
};

Home.propTypes = {
  navigation: PropTypes.shape({
    dispatch: PropTypes.func,
  }).isRequired,
};


const styles = StyleSheet.create({
  container: {
    flex: 1,
    marginTop: 20,
  },
  item: {
    backgroundColor: '#f9c2ff',
    padding: 20,
    marginVertical: 8,
    marginHorizontal: 16,
  },
  title: {
    fontSize: 32,
  },
});
