import styled from 'styled-components'
import { FlatList } from 'react-native'


export const Title = styled.label`
    background-color: #C4170C;
    width: 100%;
    font-weight: bold;
    text-align: center;
    margin-top: 10px;
    /* padding: 10px; */
    font-size: 20px;
    color: #fff;
    line-height: 21px;
`;

export const ContainerNoticia = styled.div`
    flex: 1;
    margin-top: 15px;
   
    div {
        background-color: '#f9c2ff';
        margin: 10px;
        /* margin: 8px 16px; */
    }

    label: {
        font-size: 32;
    }

    img {
        width: 250px;
        height: 120px;
    }

`
