import React, { useState, useEffect } from 'react'
import { TouchableOpacity, Text, SafeAreaView, View, FlatList, StyleSheet } from 'react-native'
import PropTypes from 'prop-types'

import api from '../../services/api'
import { deleteUser } from '../../utils'
import ProductItem from '../../components/ProductItem'

import { Container, Title, Button, ButtonText, ProductList } from './styles'


const g1 = [
  {
    id: 'bd7acbea-c1b1-46c2-aed5-3ad53abb28ba',
    img: 'https://s2.glbimg.com/I6uN45otNyBJ3Ui_oEwuKzxv3dg=/0x0:5472x3648/1600x0/smart/filters:strip_icc()/i.s3.glbimg.com/v1/AUTH_59edd422c0c84a879bd37670ae4f538a/internal_photos/bs/2020/z/A/clZTvgSAK8Qao0FMA4Sw/ap20122730524206.jpg',
    title: 'First Item',
  },
  {
    id: '3ac68afc-c605-48d3-a4f8-fbd91aa97f63',
    img: 'https://s2.glbimg.com/mnlPCtH8hYeamx64zmQL26ICBic=/0x0:1973x1441/1000x0/smart/filters:strip_icc()/i.s3.glbimg.com/v1/AUTH_59edd422c0c84a879bd37670ae4f538a/internal_photos/bs/2020/x/n/25OGFqTbe6k1jB9JkcHw/os-ex-presidentes.jpg',
    title: 'Second Item',
  },
  {
    id: '58694a0f-3da1-471f-bd96-145571e29d72',
    img: 'https://s2.glbimg.com/6J_VufKuKDkgBZrXnrP4i5S9ijs=/0x0:4000x2784/1600x0/smart/filters:strip_icc()/i.s3.glbimg.com/v1/AUTH_59edd422c0c84a879bd37670ae4f538a/internal_photos/bs/2020/U/R/IYHfpmRTatLcgoEnolyQ/000-1qy0s6.jpg',
    title: 'Third Item',
  },
];

const ge = [
  {
    id: 'bd7acbea-c1b1-46c2-aed5-3ad53abb28ba',
    img: 'https://s2.glbimg.com/RLMr5sgmzWjSHl-logVKMHoUdHA=/0x0:1080x608/1600x0/smart/filters:strip_icc()/i.s3.glbimg.com/v1/AUTH_bc8228b6673f488aa253bbcb03c80ec5/internal_photos/bs/2020/I/d/U8FrqZTCqRRXr8qnkmBA/carrossel-virada.jpg',
    title: 'First Item',
  },
  {
    id: '3ac68afc-c605-48d3-a4f8-fbd91aa97f63',
    img: 'https://s2.glbimg.com/0l8cbOgBmNUzbKnR34Ly1JI6LxY=/0x0:925x616/1000x0/smart/filters:strip_icc()/i.s3.glbimg.com/v1/AUTH_bc8228b6673f488aa253bbcb03c80ec5/internal_photos/bs/2020/e/t/aA2F3fRlA50e4wuMUuOg/7.jpg',
    title: 'Second Item',
  },
  {
    id: '58694a0f-3da1-471f-bd96-145571e29d72',
    img: 'https://s2.glbimg.com/lRZjSXIIAoQMV-2mVjYWo6EjmzI=/0x0:1024x576/1000x0/smart/filters:strip_icc()/s.glbimg.com/es/ge/f/original/2019/01/23/46126084454_af130fb304_o.jpg',
    title: 'Third Item',
  },
];

const gshow = [
  {
    id: 'bd7acbea-c1b1-46c2-aed5-3ad53abb28ba',
    img: 'https://s2.glbimg.com/AYZGJe6GcNhqknuGJZVijw8wcFQ=/0x0:1125x634/1000x0/smart/filters:strip_icc()/i.s3.glbimg.com/v1/AUTH_e84042ef78cb4708aeebdf1c68c6cbd6/internal_photos/bs/2020/B/S/81uzxERXKlZq1I4HEdTQ/whatsapp-image-2020-05-01-at-19.34.32.jpeg',
    title: 'First Item',
  },
  {
    id: '3ac68afc-c605-48d3-a4f8-fbd91aa97f63',
    img: 'https://s2.glbimg.com/aeJ1H16hhaT2gIl570JmiCGzYJE=/0x0:1366x699/1600x0/smart/filters:strip_icc()/i.s3.glbimg.com/v1/AUTH_e84042ef78cb4708aeebdf1c68c6cbd6/internal_photos/bs/2020/7/e/o0BUSKRoiYLAnWiNkspw/claudia-raia.png',
    title: 'Second Item',
  },
  {
    id: '58694a0f-3da1-471f-bd96-145571e29d72',
    img: 'https://s2.glbimg.com/YoRFQUFqBEBAahUoHEqW6m37_7w=/0x0:1062x709/1000x0/smart/filters:strip_icc()/i.s3.glbimg.com/v1/AUTH_e84042ef78cb4708aeebdf1c68c6cbd6/internal_photos/bs/2020/j/L/X6d1TKQRS5akekQwj4Bw/fabianakarlareal-69468200-145329693383779-3668294356518775423-n.jpg',
    title: 'Third Item',
  },
];

function Item({ title, img }) {
  return (
    <View style={styles.item}>
      <Text style={styles.title}>{title}</Text>
      <img src={img} width="250" height="120" />
    </View>
  );
}


export default function Home() {
  // const [data, setData] = useState([]);
  // const [refreshing, setRefreshing] = useState(false);

  // useEffect(() => {
  //   async function loadProducts() {

  //     const response = await api.get('/products')

  //     console.log(response.data)

  //     setData(response.data.products);
  //   }

  //   loadProducts();
  // }, []);

  // renderListItem = ({ item }) => <ProductItem product={item} />

  return (
    <Container>
      {/* <ProductList
        // data={data}
        // keyExtractor={item => String(item.id)}
        // renderItem={renderListItem}
        // onRefresh={loadProducts}
        // refreshing={refreshing}
      /> */}

      <Title>G1</Title>
      <SafeAreaView style={styles.container}>
        <FlatList
          data={g1}
          renderItem={({ item }) => <Item title={item.title, item.img} />}
          keyExtractor={item => item.id}
        />
      </SafeAreaView>

      <Title>GloboEsporte</Title>

      <Title>GShow</Title>

      <img src="" width="200" height="80" />
    

    </Container>
  );
}

Home.navigationOptions = ({ navigation }) => {

  return {
    title: 'Home',
    headerBackTitleVisible: true,
    headerRight: () => (
      <TouchableOpacity
        onPress={() => (
          deleteUser().then(() => {
            navigation.navigate('AuthLoading')
          })
        )}
        style={{ marginRight: 10 }}
      >
        <Text>Sair</Text>
      </TouchableOpacity>
    ),
  };
  
};

Home.propTypes = {
  navigation: PropTypes.shape({
    dispatch: PropTypes.func,
  }).isRequired,
};


const styles = StyleSheet.create({
  container: {
    flex: 1,
    marginTop: 20,
  },
  item: {
    backgroundColor: '#f9c2ff',
    padding: 20,
    marginVertical: 8,
    marginHorizontal: 16,
  },
  title: {
    fontSize: 32,
  },
});
