import React, { useState, useEffect } from 'react'
import { TouchableOpacity, Text, SafeAreaView, View, FlatList, StyleSheet } from 'react-native'
import PropTypes from 'prop-types'
import { ScrollView } from 'react-native-gesture-handler'


import api from '../../services/api'
import { deleteUser } from '../../utils'

import ProductItem from '../../components/ProductItem'
import G1 from '../../components/G1'
import GloboEsporte from '../../components/GloboEsporte'
import GShow from '../../components/GShow'

import ImageLogo  from '../../images/logo.svg'

import { Container, TitleG1, TitleGe, TitleGs, Button, ButtonText, ProductList, Tabs } from './styles'



export default function Home() {
  // const [data, setData] = useState([]);
  // const [refreshing, setRefreshing] = useState(false);

  // useEffect(() => {
  //   async function loadProducts() {

  //     const response = await api.get('/products')

  //     console.log(response.data)

  //     setData(response.data.products);
  //   }

  //   loadProducts();
  // }, []);

  // renderListItem = ({ item }) => <ProductItem product={item} />

  return (
    <>
    <Tabs>
      <Button >
      {true ? (
            <ButtonText size="small" color="#FFF" />
          ) : (
            <ButtonText>Noticias</ButtonText>
          )}
      </Button>
      <Button >
          <ButtonText>Feed</ButtonText>
      </Button>
    </Tabs>

    <ScrollView>
      <Container>
        {/* <ProductList
          // data={data}
          // keyExtractor={item => String(item.id)}
          // renderItem={renderListItem}
          // onRefresh={loadProducts}
          // refreshing={refreshing}
        /> */}

        {/* <TitleG1>G1</TitleG1> */}

        <G1 />
        <GloboEsporte />
        <GShow />
    
      

      </Container>
    </ScrollView>
    </>
  );
}



Home.navigationOptions = ({ navigation }) => {

  return {
    title: 'Home',
    headerTitle: <img src={ImageLogo} width="120" height="40" style={{ alignSelf: 'center' }}/>,
    headerBackTitleVisible: true,
    headerRight: () => (
      <TouchableOpacity
        onPress={() => (
          deleteUser().then(() => {
            navigation.navigate('AuthLoading')
          })
        )}
        style={{ marginRight: 10 }}
      >
        <Text>Sair</Text>
      </TouchableOpacity>
    ),
  };
  
};

Home.propTypes = {
  navigation: PropTypes.shape({
    dispatch: PropTypes.func,
  }).isRequired,
};
